window-lua.code:
local   eventId = 1

function create(data,context)

    local   events = {}
    local   sensorinfo = esp_parseJsonFrom("sensorinfo")

    for index,value in ipairs(sensorinfo)
    do
        local   e = {}

        e.id = tostring(eventId)
        e.device_id = value.device_id
        e.sensor_id = value.sensor_id
        e.value = value.value
        events[index] = e

        eventId = eventId + 1
    end

    return(events)
end
